-- Demo dataset for EV Journey (Northern Thailand)
-- Safe to re-run: uses upsert and clears only the demo agent ids.
SET client_encoding = 'UTF8';
CREATE EXTENSION IF NOT EXISTS postgis;

-- Provinces (base list referenced by the app)
ON CONFLICT (slug_en) DO UPDATE SET name_th = EXCLUDED.name_th;

-- Clean previous demo rows so the script can be re-run
DELETE FROM agent_logs WHERE agent_id IN (9001, 9002);
DELETE FROM agent_routes WHERE agent_id IN (9001, 9002);
DELETE FROM agents WHERE id IN (9001, 9002);
DELETE FROM chargers WHERE id IN ('demo-cnx-old-town', 'demo-cnx-airport-ac', 'demo-lpn-riverside', 'demo-lpg-night-bazaar', 'demo-mhs-airport');
DELETE FROM foods WHERE id IN ('demo-food-khaosoi', 'demo-food-lamphun-noodle', 'demo-food-lampang-steak', 'demo-food-mhs-pai');
DELETE FROM cafes WHERE id IN ('demo-cafe-ristr8to', 'demo-cafe-lpn-rimping', 'demo-cafe-lpg-kadkongta', 'demo-cafe-pai-canyon');
DELETE FROM hotels WHERE id IN ('demo-hotel-chiangmai-orchid', 'demo-hotel-lamphun-river', 'demo-hotel-lampang-green', 'demo-hotel-mhs-river');
DELETE FROM attractions WHERE id IN ('demo-at-cnx-suthep', 'demo-at-lpn-bridge', 'demo-at-lpg-temple', 'demo-at-mhs-pai-canyon');

-- Chargers
ON CONFLICT (id) DO UPDATE SET
  name = EXCLUDED.name,
  type = EXCLUDED.type,
  kw = EXCLUDED.kw,
  capacity = EXCLUDED.capacity,
  lat = EXCLUDED.lat,
  lon = EXCLUDED.lon,
  province_id = EXCLUDED.province_id,
  brand = EXCLUDED.brand,
  address = EXCLUDED.address;

